 * @link       http://s2base.php5.seasar.org/
 * @since      Class available since Release 1.0.3
 * @package    org.seasar.s2base.zf.controller
 * @author     klove
 */
abstract class S2Base_ZfAbstractDispatcher extends Zend_Controller_Dispatcher_Standard {

    /**
     * @param Zend_Controller_Request_Abstract $request
     * @param string $moduleName
     * @param string $controllerClassName
     * @return Zend_Controller_Action
     */
    abstract protected function instantiateController(Zend_Controller_Request_Abstract $request, $moduleName, $controllerClassName);

    /**
     * @see Zend_Controller_Dispatcher_Standard::dispatch()
     */
    public function dispatch(Zend_Controller_Request_Abstract $request, Zend_Controller_Response_Abstract $response)
    {
        $this->setResponse($response);

        /**
         * Get controller class
         */
        if (!$this->isDispatchable($request)) {
            if (!$this->getParam('useDefaultControllerAlways')) {
                require_once 'Zend/Controller/Dispatcher/Exception.php';
                throw new Zend_Controller_Dispatcher_Exception('Invalid controller specified (' . $request->getControllerName() . ')');
            }

            $className = $this->getDefaultControllerClass($request);
        } else {
            $className = $this->getControllerClass($request);
            if (!$className) {
                $className = $this->getDefaultControllerClass($request);
            }
        }

        /**
         * Load the controller class file
         */
        $className = $this->loadClass($className);

        $controller = $this->instantiateController($request,
                                                   $request->getModuleName(),
                                                   $className);

        /**
         * Retrieve the action name
         */
        $action = $this->getActionMethod($request);

        /**
         * Dispatch the method call
         */
        $request->setDispatched(true);

        // by default, buffer output
        $disableOb = $this->getParam('disableOutputBuffering');
        $obLevel   = ob_get_level();
        if (empty($disableOb)) {
            ob_start();
        }

        try {
            $controller->dispatch($action);
        } catch (Exception $e) {
            // Clean output buffer on error
            $curObLevel = ob_get_level();
            if ($curObLevel > $obLevel) {
                do {
                    ob_get_clean();
                    $curObLevel = ob_get_level();
                } while ($curObLevel > $obLevel);
            }

            throw $e;
        }

        if (empty($disableOb)) {
            $content = ob_get_clean();
            $response->appendBody($content);
        }

        // Destroy the page controller instance and reflection objects
        $controller = null;
    }
}
?>
